<?phprequire_once 'support_file.php';$title='Chart of Accounts';$separator=$_SESSION['separator'];?><link href="../css/sitemapstyler.css" rel="stylesheet" type="text/css" media="screen" /><script type="text/javascript" src="../js/sitemapstyler.js"></script><script type="text/javascript">function changeBox(cbox) {	box = eval(cbox);	box.checked = !box.checked;}$(function() {	$("#sitemap").treeview({		collapsed: true,		animated: "medium",		control:"#sidetreecontrol",		persist: "location"	});})</script><script src="../js/jquery.js" type="text/javascript"></script><script src="../js/jquery.treeview.js" type="text/javascript"></script><style>h1{	font-size:140%;	margin:0 20px;	line-height:80px;}p{	margin:0 auto;	width:680px;	padding:1em 0;}</style><table>	  <tr>		<td align="right"><? //include('PrintFormat.php');?></td>	  </tr></table><table id="grp" class="tabledesign" cellspacing="0"><tr><td><form id="form1" name="form1" method="get" action="">	<div align="right">	  <div id="sidetreecontrol">	  <a href="#"><input type="button" name="Button" value="Collapse All" /></a> 	  <a href="?#"><input type="button" name="Button" value="Expand All" /></a></div> 	  <input name="show" type="submit" id="show" value="Show Report" />	  <input type="submit" name="add" id="add" value="Add" />	</div>	<div id="container">	<div id="content">			<ul id="sitemap"><?$sql='select * from ledger_group order by group_id';if($_SESSION['user']['group']>1)$sql='select * from ledger_group where group_for="'.$_SESSION['user']['group'].'" order by group_id';$query=mysql_query($sql);if(mysql_num_rows($query)>0){while($grp=mysql_fetch_object($query)){$grp_id=(string)($grp->group_id*100000000);?><li><label for="gid<?=$grp->group_id;?>"><a href="" onClick="changeBox('document.form1.gid<?=$grp->group_id;?>');return false;"><?=ledger_sepe($grp_id,$separator)?><?=' '.$grp->group_name;?></a><input name="gid" id="gid<?=$grp->group_id;?>" type="radio" value="<?=$grp->group_id;?>"  style="visibility:hidden;"/></label